import React from 'react'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

function UsuarioEspecifico(props) {

    // Aqui, pegamos o id que é passado pela URL (id do usuário específico)
    let id = null;

    if (props.match.path.toLowerCase().includes('editar')) {
        id = props.match.params.id
    }

    // MÉTODO GET
    const [usuario, setUsuario] = useState([])
    
    
    // Effect para trazer todo conteúdo do objeto (nesse caso, de um OBJETO ESPECÍFICO e setando no state acima)
    useEffect(() => {
        fetch("/rest/user/" + id).then((resp) => {
            return resp.json()
        }).then((resp) => {
            setUsuario(resp)
            console.log(resp)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    // Acho que esse Effect faz a mesma coisa que o effect de cima
    useEffect(() => {
        if (id) {
            fetch("/rest/user/" + id).then(resp => {
                return (resp.json())
            }).then(dados => {
                setUsuario(dados)
            })
        }
    }, [id])

    const handleChange = (e) => {
        setUsuario({ ...usuario, [e.target.name]: e.target.value })
    }


    const [mostraForm, setMostraForm] = useState(false)

    const showForm = () => {
        setMostraForm(!mostraForm)
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        fetch("/rest/user/" + id, {
            method: "put",
            headers: {
                'Content-Type': 'application/json'
            }, body: JSON.stringify(usuario)
        }).then(() => {
            window.location = "/user"
        })
    }



    return (
        <div>
            <h1>Usuário Específico</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>Senha</th>
                        <th>*****</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{usuario.id}</td>
                        <td>{usuario.nome}</td>
                        <td>{usuario.email}</td>
                        <td>{usuario.senha}</td>
                        <button onClick={showForm}>Editar</button>
                    </tr>

                </tbody>
            </table>
            <div>
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <h3>Olá, {usuario.nome}!</h3>
                <span>Deseja responder o questionário de Avaliação?</span>
                <Link title="questionario" to={`/quest/${usuario.id}`}>Clique aqui!</Link>
            </div>
            <div>
                {mostraForm && (
                    <form onSubmit={handleSubmit}>
                        <fieldset>
                            <legend>Novo Usuário</legend>
                            <div>
                                <input type='text' name="nome" placeholder='Nome'
                                    value={usuario.nome} onChange={handleChange} />Nome
                                <input type='text' name="email" placeholder='Email'
                                    value={usuario.email} onChange={handleChange} />Email
                                <input type='text' name="senha" placeholder='Senha'
                                    value={usuario.senha} onChange={handleChange} />Senha
                                <input type="submit" />Enviar
                            </div>
                        </fieldset>

                    </form>)}
            </div>
        </div>

    )
}

export default UsuarioEspecifico
