import styled from 'styled-components';

export const StyledImg = styled.img`
    border-radius: 15%;
    width: 30%;
    padding: 10px;
    margin: 20px;
`;
export const DivCart  = styled.div`
    display:block;
    width:69%;
    margin:5px;
    margin-left:110px;
    padding:20px;
`;

export const StyledCaterinha = styled.div`

   div{
    border:1px solid black;
    margin:15px;
    width:80%
   }
    font-family:sans-serif;
    h3, p {
    display:flex;
    color:red;
    font-weight:bold;
    font-size:18px;
    margin:10px;
    border:1px solid black;
    }
    img {
    border-radius: 15%;
    width: 25%;
    padding: 10px;
    margin: 20px;
    }
`;

export const Alunos = styled.div`
    color:red;
    font-weight:bold;
    font-size:18px;
    font-family:sans-serif;
    border:1px solid black;
    margin:0;
    padding:5px;
    margin-left:14px;
    input {
        border:1px solid black;
        width:99%
    }
    p {
        color: blue;
        border:1px solid black;
        width:99%
    }
`;
