import React from 'react'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'



function FormProduto(props) {

    let id = null

    if (props.match.path.toLowerCase().includes('editar')) {
        id = props.match.params.id;

    }
    const [novo, setNovo] = useState({
        codigo: id,
        titulo: "",
        preco: "",
        qtd: ""
    })

    let metodo = "post"
    if (id) {
        metodo = "put"
    }

    const handleChange = (e) => {
        setNovo({ ...novo, [e.target.name]: e.target.value })
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        fetch("/rest/produto/" + (id ? id : ""), {
            method: metodo,
            headers: {
                'Content-Type': 'application/json'
            }, body: JSON.stringify(novo)
        }).then(() => {
            window.location = "/"
            alert("espera ae")
        })
    }

    useEffect(() => {
        if (id) {
            fetch("/rest/produto/" + id).then(resp => {
                return (resp.json())
            }).then(data => {
                setNovo(data)
            })
        }

    }, [id])



    return (
        <div>
            <h1>Formulário de Produtos!</h1>
            <form onSubmit={handleSubmit}>
                <fieldset>
                    <legend>Produtos</legend>
                    <div>
                        <input type="text" name="titulo" value={novo.titulo} placeholder="Titulo" onChange={handleChange} />
                    </div>
                    <div>
                        <input type="number" name="preco" value={novo.preco} placeholder="Preço" onChange={handleChange} />
                    </div>
                    <div>
                        <input type="number" name="quantidade" value={novo.qtd} placeholder="Quantidade" onChange={handleChange} />
                    </div>
                    <div>
                        <button type="submit">Enviar</button>
                    </div>
                    <Link to="/">Cancelar</Link>
                </fieldset>
            </form>

        </div>
    )
}

export default FormProduto
