import styled from 'styled-components';
import sinal from '../../img/sinal.jpg';

const vermelho = "#ff0000";
const amarelo = "#ffff00";
const verde = "#00ff00";
const preto = "#000000";
const branco = "#ffffff";


export const DivSem = styled.div`
  color: ${branco};
  text-shadow: 2px 2px ${preto};
  .red {
    color: ${vermelho};
  }
  .yellow {
    color: ${amarelo};
  }
  .green {
    color: ${verde};
  }
  h3{
    font-size: 5rem;
    margin: 0;
    padding: 0.5rem;
  }
`;

export const Sem = styled.div`
  display: flex;
  justify-content:center;
  background: url(${sinal}) top ;
  padding:6rem;
`;

export const Btn = styled.button`
  margin-top:6rem;
  padding: 1.5%;
  font-size: 1.5em;
  font-weight: bold;
  color: ${preto};
  background-color: ${branco};
  border-radius:15%;
  position:absolute;
`;
