import React from 'react'
import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'


function ListaProduto() {

    const [produtos, setProdutos] = useState([])

    useEffect(() => {
        fetch("/rest/produto").then((resp) => {
            return resp.json()
        }).then((resp) => {
            setProdutos(resp)
            console.log(resp)
        }).catch((error) => {
            console.log(error)
        })
        // TUDO QUE ESTIVER AQUI DENTRO, VOU EXECUTAR, DIZ A ARROW FUNCTION, NA ORDEM
    }, [])

    const controlaProduto = () => {

    }


    const handleDelete = (id) => {
        fetch("/rest/produto/" + id, {
            method: "delete"
        }).then(() => {
            window.location = "/"
        }).catch((error) => {
            console.log(error)
        })
    }

    return (
        <div>
            <h1>Lista de Produtos</h1>
            <Link to="/incluir">ADD PRODUTO</Link>
            <table>
                <thead>
                    <tr>
                        <th>Título</th>
                        <th>Preço</th>
                        <th>Quantidade</th>
                        <th>***</th>
                    </tr>
                </thead>
                <tbody>
                    {produtos.map((produto) => (
                        <tr key={produto.codigo}>
                            <td>{produto.titulo}</td>
                            <td>R$ {produto.preco}</td>
                            <td>{produto.qtd > 2 ? produto.qtd : 'Menor que 1...'}</td>

                            <td>
                                <Link title="Editar" to={`/editar/${produto.codigo}`}>Editar</Link> |
                                <Link title="Excluir" to="/" onClick={handleDelete.bind(this, produto.codigo)}>Excluir</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
                <tfoot>

                </tfoot>
            </table>
            <table>
                <thead>
                    <tr>
                        <th>Título</th>
                        <th>Preço</th>
                        <th>Quantidade</th>
                        <th>***</th>
                    </tr>
                </thead>
                <tbody>
                    {produtos.map(produto => {
                        if (produto.codigo > 2) {
                            return [<td>{produto.titulo}</td>,
                                <td>{produto.preco}</td>,
                                <td>{produto.qtd}</td>]
                        }
                    })}
                </tbody>
                <tfoot>
                </tfoot>
            </table>

        </div>
    )
}

export default ListaProduto;
