import React, { Component } from 'react';
import Comentario from '../../components/comentario/Comentario';

import {Container, Button} from '../../components/comentario/StyledPageComentario';


class PageComentarios extends Component {
  state = {
    comentarios: [
      {
        nome: 'Rafael',
        data: new Date(2020, 9, 19, 17, 30, 0),
        mensagem: 'Olá, tudo bem?'
      },
      {
        nome: 'Pedro',
        data: new Date(2020, 9, 19, 17, 30, 0),
        mensagem: 'Olá, tudo bem. E você?'
      }
    ],
    novoComentario: {
      nome: '',
      mensagem: ''
    }
  }

  adicionarComentario = evento => {
    evento.preventDefault();
    const novoComentario = { ...this.state.novoComentario, data: new Date() }
    this.setState({
      comentarios: [...this.state.comentarios, novoComentario],
      novoComentario: { nome: '', mensagem: '' }
    })
  }

  removerComentario = comentario => {
    let lista = this.state.comentarios;
    lista = lista.filter(c => c !== comentario)
    this.setState({ comentarios: lista })
  }

  digitacao = evento => {
    const { name, value } = evento.target;
    this.setState({ novoComentario: { ...this.state.novoComentario, [name]: value } })
  }

  render() {
    return (
      <Container >
        <h1>comentarios</h1>
        {this.state.comentarios.map((comentario, indice) => (
          <Comentario
            key={indice}
            nome={comentario.nome}
            data={comentario.data}
            onRemove={this.removerComentario.bind(this, comentario)}>
            {comentario.mensagem}
          </Comentario>
        ))}

        <form method="post" action="aqui vai a função de enviar os dados do formulario" onSubmit={this.adicionarComentario}>
          <div>
            <input
              type="text"
              name="nome"
              value={this.state.novoComentario.nome}
              onChange={this.digitacao}
              required
              placeholder="Digite seu nome" />
          </div>
          <div>
            <textarea
              name="mensagem"
              value={this.state.novoComentario.mensagem}
              onChange={this.digitacao}
              required
              rows="4" 
              placeholder="Escreva um comentário..."/>
          </div>
          <Button type="submit">Publicar Comentário</Button>
        </form>
      </Container>
    );
  }
}

export default PageComentarios;
