import React from 'react';
import { useState } from 'react';

import {DivSem, Sem, Btn} from './StyleSemaforo';


const Semaforo = () => {

  const sinal = ['PARE', 'ATENÇÃO', 'SIGA']
  const [i, setSinal] = useState(0)

  function show() {
    if (i === 0) {
      document.getElementById("color").classList.remove('red');
      document.getElementById("color").classList.add('yellow');
      setSinal(i + 1)
    } else if (i === 1) {
      document.getElementById("color").classList.remove('yellow');
      document.getElementById("color").classList.add('green');
      setSinal(i + 1)
    } else if (i === 2) {
      document.getElementById("color").classList.remove('green');
      document.getElementById("color").classList.add('red');
      setSinal(0)
    }
  }

  return (
    <>
      <DivSem>
        <Sem>
          <div id="color">
            <h3>{sinal[i]}</h3>
          </div>
          <Btn onClick={() => show()}>Exibição</Btn>
        </Sem>
      </DivSem>
    </>

  );
}
export default Semaforo;
