import React, { useState } from "react";
import {
  Form,
  FormWrapper,
  FormHeader,
  FormBody,
  FormFieldset,
  FormInput,
  FormButton,
  FormLink,
} from "../../assets/style/StyledFormGlobal";

function FormLogin() {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const [status, setStatus] = useState({
    type: "",
    mensagem: "",
  });

  //Receber os dados do formulário
  const valueInput = (e) =>
    setUser({ ...user, [e.target.name]: e.target.value });

  //Enviar os dados para o back-end
  const addUser = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    const saveDataForm = true;
    if (saveDataForm) {
      setStatus({
        type: "success",
        mensagem: "Logado com sucesso Bem vindo",
      });
      setUser({
        email: "",
        password: "",
      });
    } else {
      setStatus({
        type: "error",
        mensagem: "Erro: Usuário não cadastrado!",
      });
    }
  };

  function validate() {
    if (!user.email)
      return setStatus({
        type: "error",
        mensagem: "Erro: Necessário preencher o campo e-mail!",
      });
    if (!user.password)
      return setStatus({
        type: "error",
        mensagem: "Erro: Necessário preencher o campo senha!",
      });
    if (user.password.length < 6)
      return setStatus({
        type: "error",
        mensagem: "Erro: A senha precisa ter pelo menos seis caracteres!",
      });

    return true;
  }

  const isLogado = JSON.parse(localStorage.getItem("isLogado") || "[]");

  function someLogin() {
    if (user.email && user.password && user.password.length >= 6) {
      return (
        (document.getElementById("form").style.visibility = "hidden"),
        window.location.replace("/editar"),
        isLogado.push({
          logado: true,
        }),
        localStorage.setItem("isLogado", JSON.stringify(isLogado))
      );
    }
  }

  return (
    <FormWrapper>
      <FormBody>
        <FormHeader>
          <h2>Login Okoshi</h2>
        </FormHeader>

        {status.type === "success" ? (
          <p style={{ color: "#08b842fd" }}>{status.mensagem}</p>
        ) : (
          ""
        )}
        {status.type === "error" ? (
          <p style={{ color: "#ff0000" }}>{status.mensagem}</p>
        ) : (
          ""
        )}

        <Form onSubmit={addUser} id="form">
          <FormFieldset>
            <label>E-mail:</label>
            <FormInput
              type="email"
              name="email"
              placeholder="Email cadastrado"
              onChange={valueInput}
              value={user.email}
              required
            />
          </FormFieldset>

          <FormFieldset>
            <label>Senha: </label>
            <FormInput
              type="password"
              name="password"
              placeholder="Senha Cadastrada"
              autoComplete="on"
              onChange={valueInput}
              value={user.password}
            />
          </FormFieldset>

          <FormFieldset>
            <FormButton type="submit" onClick={() => someLogin()}>
              Login
            </FormButton>
          </FormFieldset>
        </Form>

        <FormFieldset>
          <FormLink href="/cadastro">
            Não tem uma conta Clique aqui e junte-se a nós!
          </FormLink>
        </FormFieldset>

        <FormFieldset>
          <FormLink href="/forgetPassword">Esqueceu sua senha!</FormLink>
        </FormFieldset>
      </FormBody>
    </FormWrapper>
  );
}
export default FormLogin;
