import React, { useState } from 'react';
import Phill from '../../img/phill.png'
import Akemi from '../../img/akemi.jpg'
import Maciel from '../../img/marcosmaciel.jpg'
import Vinicius from '../../img/marcosvinicius.jpg'

import { DivCart, Alunos, StyledImg, StyledCaterinha } from './StyleCarteirinha';



const nomeAluno = ['Vitoria Akemi', 'Marcos Maciel', 'Marcos Vinicius'];
const rmAluno = ['88077', '88267', '86904'];

const curso = ['Analise e Desenvolvimento de Sistemas'];
const turma = ['1TDSJ'];

const Carteirinha = (props) => {

    const [ultimaCarteirinha, setCarteirinha] = useState({ 'nome': '', 'rm': '', 'curso': '', 'turma': '' })

    const mostraCarteirinha = (e) => {
        const { name, value } = e.target;
        if (name === 'nome') {
            setCarteirinha({ 'nome':value,'rm':ultimaCarteirinha.rm, 'curso':ultimaCarteirinha.curso, 'turma':ultimaCarteirinha.turma })
        }
        else if (name === 'rm') {
            setCarteirinha({ 'nome': ultimaCarteirinha.nome, 'rm': value, 'curso': ultimaCarteirinha.curso, 'turma': ultimaCarteirinha.turma })
        }
        else if (name === 'curso') {
            setCarteirinha({ 'nome': ultimaCarteirinha.nome, 'rm': ultimaCarteirinha.rm, 'curso': value, 'turma': ultimaCarteirinha.turma })
        }
        else if (name === 'turma') {
            setCarteirinha({ 'nome': ultimaCarteirinha.nome, 'rm': ultimaCarteirinha.rm, 'curso': ultimaCarteirinha.curso, 'turma': value })
        }
    }

return (
    <DivCart>
        <StyledCaterinha>
            <div>
                <img src={Akemi} alt='imagem aluno Akemi'></img>
                <h3>Nome: {nomeAluno[0]}</h3>
                <p>RM: {rmAluno[0]}</p>
                <p>Curso: {curso[0]}</p>
                <p>Turma: {turma[0]}</p>
            </div>
            <div>
                <img src={Maciel} alt='imagem aluno marcos'></img>
                <h3>Nome: {nomeAluno[1]}</h3>
                <p>RM: {rmAluno[1]}</p>
                <p>Curso: {curso[0]}</p>
                <p>Turma: {turma[0]}</p>
            </div>
            <div>
                <img src={Vinicius} alt="imagem aluno Marcos Vinicus"></img>
                <h3>Nome: {nomeAluno[2]}</h3>
                <p>RM: {rmAluno[2]}</p>
                <p>Curso: {curso[0]}</p>
                <p>Turma: {turma[0]}</p>
            </div>
        </StyledCaterinha>

        {props.children}
            <Alunos>
                <div>
                    <StyledImg src={Phill} alt='professor'></StyledImg>
                    <div>
                        Nome: <input name="nome" onChange={mostraCarteirinha} placeholder="Digite seu Nome aqui" />
                        RM: <input name="rm" onChange={mostraCarteirinha} placeholder="Digite seu RM aqui"  />
                        Curso: <input name="curso" onChange={mostraCarteirinha} placeholder="Digite seu Curso aqui" />
                        Turma: <input name="turma" onChange={mostraCarteirinha} placeholder="Digite sua Turma aqui"  />
                    </div>
                    <p>Nome: {ultimaCarteirinha.nome}</p>
                    <p>RM: {ultimaCarteirinha.rm}</p>
                    <p>Curso: {ultimaCarteirinha.curso}</p>
                    <p>Turma: {ultimaCarteirinha.turma}</p>
                </div>
            </Alunos>
        </DivCart>

    );
}
export default Carteirinha;
